mapper.insert:
INSERT INTO TB_MBTI_RESULT
          (
            MBTI
          , CREATED_AT
          )
     VALUES
          (
            #{result}
          , NOW()
          )

mapper.select:
  SELECT ROUND((COUNT(*) * 100.0 / (SELECT COUNT(*) FROM TB_MBTI_RESULT)), 1) AS PERCENTAGE
    FROM TB_MBTI_RESULT TMR
   WHERE TMR.MBTI = #{result}
GROUP BY TMR.MBTI;
